import React from "react";
import ReactDOM from "react-dom/client"

import './index.css'

/*

const Greeting = () => {
    return (
        <>
            <Person />
            <Message />
        </>
    )
}

const Person = () => <h2>John Doe</h2>
const Message = () =>  {
    return <p>This is my message</p>
}
*/


// Druga wersja z React.createElement (element, props, wartosc)
/*const Greeting = () => {
    return React.createElement(h2, {}, 'hello world')
}*/


const root = ReactDOM.createRoot(document.getElementById('root'));

const BookList = (props) => {
    return (
        <section className='booklist'>
            <Book author={author} title={title} img={props.img} />
            <Book author={author} title={title} img={img} />
            <Book author={author} title={title} img={img} />
            <Book author={author} title={title} img={img} />
        </section>
    )
};


const author = 'Prince Harry';
const title = 'Spare';
const img = "./images/book1.jpg";

const Book = (props) => {
    return (
        <article className='book'>
            <Image/>
            <Title/>
            <Author/>
        </article>
    )
}
const Image = (props) => <img src={props.img} alt="book cover"/>;
const Title = (props) => {
    return <h2>{props.title}</h2>
};

const Author = (props) => {
    return <h4>{author.toUpperCase()}</h4>;
}

    root.render(<BookList />)
