}*/


const root = ReactDOM.createRoot(document.getElementById('root'));


const BookList = () => {
    return (
        <section className='booklist'>
            {books.map(({title, author, img, id}, index) => {
                return (
                    <Book key={id} img={img} author={author} title={title}/>
                )
            })}
        </section>
    )
};







